sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {


       try {
        // Get the current user making the request
        //var userSysId = gs.getUserID();

        // Fetch the query parameter 'state', if it exists
        //var stateParam = request.getQueryParameter('state');

        // Query the Approval Task table (sysapproval_approver)
        var approvalTaskGR = new GlideRecord('sysapproval_approver');
        //approvalTaskGR.addQuery('approver', userSysId);

            // Default to 'requested' state if no state is provided
            approvalTaskGR.addQuery('state', 'requested');
			approvalTaskGR.addQuery('source_table', 'x_906707_myreque_0_myrequests');

        approvalTaskGR.query();

        // Collect the results
        var approvals = [];
        while (approvalTaskGR.next()) {
            approvals.push({
                "task_number": approvalTaskGR.sysapproval.number.toString(),
                "approval_state": approvalTaskGR.state.toString(),
                "approval_user": approvalTaskGR.approver.name.toString(),
                "approval_created": approvalTaskGR.sys_created_on.toString(),
            });
        }

        // Build the response body
        var responseBody = {
            "approvals": approvals
        };

        // Set the response content type and body
        response.setContentType('application/json');
        response.setStatus(200);
        response.setBody(responseBody);

    } catch (error) {
        // Handle any errors and return a 500 status
        response.setStatus(500);
        response.setBody(JSON.stringify({error: error.message}));

    }






})(request, response);
